import { React, useState, useEffect } from 'react';
import axios from 'axios';

import { CssBaseline, Button, Container } from '@mui/material';

import Movies from './components/Movies';
import TopBar from './components/TopBar';
import MoviesForm from './components/MoviesForm';

const App = () => {

    const [movies, setMovies] = useState([]);
    const [open, setOpen] = useState(false);

    const addMovie = (movies) => {
        setMovies((prevMovies) => {
            return [...prevMovies, movies]
        })
    }

    const openForm = () => {
        setOpen(!open);
    }

    useEffect(() => {
        axios.get('http://localhost:3001/movies').then(res => {
            setMovies(res.data.moviesList);
        }).catch(err => {
            console.log(err);
        })
    }, [])

    return (
        <CssBaseline>
            <TopBar />
            <Movies moviesList={movies} />
            <Container align="center" sx={{ marginBottom: "50px" }}>
                <Button variant="contained" onClick={openForm} >Add New Movie</Button>
            </Container>
            {open ? <MoviesForm addMovie={addMovie} /> : <></>}
        </CssBaseline>
    )
}

export default App;
