import { React, useState } from 'react';
import { Container, Box, Typography, CircularProgress } from '@mui/material';

import MoviesList from './MoviesList';

const Movies = (props) => {

    if (props.moviesList.length === 0) {
        return (<Container align="center" sx={{ width: 1200, height: 800, marginTop: "100px", marginBottom: "100px", mx: "auto", maxWidth: "md" }}>
            <Box component="div" sx={{ border: "3px solid black", borderRadius: "10px", backgroundColor: "white", display: "flex", justifyContent: "center" }}>
                <div style={{ position: "absolute", left: "50%", top: "50%", transform: "translate(-50%, -50%)" }}>
                    <CircularProgress />
                </div>
            </Box>
        </Container>
        )
    }

    return (
        <Container align="center" sx={{ width: 1200, marginTop: "100px", marginBottom: "50px", mx: "auto", maxWidth: "md" }}>
            <Box sx={{ border: "3px solid black", borderRadius: "10px", backgroundColor: "#757575" }}>
                <MoviesList movies={props.moviesList} />
            </Box>
        </Container>
    )
};

export default Movies;
